#mapa-polos {
    height: 88vh;
    z-index: 1;
}

.estilo-tooltip-polo {
    background: red !important;
    color: white !important;
    border: 0 !important;
    font-size: 15px;
    border-radius: 20px !important;
    width: 30px;
    height: 30px;
    text-align: center;
    line-height: 18px;
    font-family: "Roboto Condensed Bold", sans-serif;
}

.estilo-popup-polo .leaflet-popup-tip {
    width: 0;
    height: 0;
}

.estilo-popup-polo .leaflet-popup-content-wrapper {
    position: relative;
    background: #343a40;
    color: rgba(255, 255, 255, 0.8);
    border-radius: 0;
    font-family: "Roboto condensed", sans-serif;
    font-size: 16px;
    box-shadow: none;
}

.leaflet-tooltip-left.estilo-tooltip-polo::before {
    border-left-color: transparent !important;
}

.leaflet-tooltip-right.estilo-tooltip::before {
    border-right-color: transparent !important;
}
